Time Complexity:  O(mn), Space: O(mn)
<code>
public class Solution {
    public boolean isMatch(String s, String p) {
        if (s == null || p == null) {
            return false;
        }
        int m = s.length();
        int n = p.length();
        boolean[][] result = new boolean[m + 1][n + 1];
        result[0][0] = true;
        for (int i = 1; i &lt;= m; i++) {
            result[i][0] = false;
        }
        for (int i = 1; i &lt;= n; i++) {
            if (p.charAt(i - 1) == '*') {
                result[0][i] = result[0][i - 2];
            }
        }
        for (int i = 1; i &lt;= m; i++) {
            for (int j = 1; j &lt;= n; j++) {
                char c1 = s.charAt(i - 1);
                char c2 = p.charAt(j - 1);
                if (c2 == '*') {
                    result[i][j] = result[i][j - 2] || match(c1, p.charAt(j - 2)) && result[i][j - 2] || match(c1, p.charAt(j - 2)) && result[i - 1][j];
                } else {
                    result[i][j] = match(c1, c2) && result[i - 1][j - 1];
                }
            }
        }
        return result[m][n];
    }
    public boolean match(char c1, char c2) {
        return c2 == '.' || c2 == c1;
    }
}
</code>

